%sql 
-- Bar graph of EDUCATION (Groups) and Total (sum) (Values, no Keys.   May need to remove TOTAL from Keys.

Select sum(cust_id) as total, education from SH.SUPPLEMENTARY_DEMOGRAPHICS group by Education;

%script
-- Create SUPPLEMENTARY_DEMOGRAPHICS2 View that remove COMMENTS unstructured data for simplicity

BEGIN
      EXECUTE IMMEDIATE 'DROP Table SUPPLEMENTARY_DEMOGRAPHICS2';
  EXCEPTION
      WHEN OTHERS THEN NULL;
END;
/
-- Create SUPPLEMENTARY_DEMOGRAPHICS2 table that removes unstructed text "COMMENTS" attribute.

CREATE Table SUPPLEMENTARY_DEMOGRAPHICS2 
  AS (SELECT AFFINITY_CARD, BOOKKEEPING_APPLICATION, BULK_PACK_DISKETTES, CUST_ID, EDUCATION, FLAT_PANEL_MONITOR, HOME_THEATER_PACKAGE, HOUSEHOLD_SIZE, OCCUPATION, OS_DOC_SET_KANJI, PRINTER_SUPPLIES, YRS_RESIDENCE, Y_BOX_GAMES
      FROM SH.SUPPLEMENTARY_DEMOGRAPHICS);

%script

-- Find the importance of attributes that independently impact the target attribute:  AFFINITY_CARD (high responders)

DECLARE
v_sql varchar2(100);

BEGIN

BEGIN EXECUTE IMMEDIATE 'DROP TABLE ai_explain_output';
EXCEPTION WHEN OTHERS THEN NULL; 
END;

BEGIN
  DBMS_PREDICTIVE_ANALYTICS.EXPLAIN(
    data_table_name     => 'SUPPLEMENTARY_DEMOGRAPHICS2',
    explain_column_name => 'AFFINITY_CARD',
    result_table_name   => 'ai_explain_output');
END;

End;
