%script
-- Build a classification model and then generate a lift test result and an apply result.  

DECLARE
v_sql varchar2(100);

BEGIN


-- drop build settings
BEGIN
v_sql := 'DROP TABLE Wine_build_settings PURGE';
EXECUTE IMMEDIATE v_sql;
DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
WHEN OTHERS THEN
DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no table exists');
END;

-- drop model
BEGIN
v_sql := 'CALL DBMS_DATA_MINING.DROP_MODEL(''Wine_CLASS_MODEL'')';
EXECUTE IMMEDIATE v_sql;
DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
WHEN OTHERS THEN
DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no model exists');
END;

-- drop apply result
BEGIN
v_sql := 'DROP TABLE Wine_APPLY_RESULT PURGE';
EXECUTE IMMEDIATE v_sql;
DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
WHEN OTHERS THEN
DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no table exists');
END;

-- drop lift result 
BEGIN
v_sql := 'DROP TABLE Wine_LIFT_TABLE PURGE';
EXECUTE IMMEDIATE v_sql;
DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
WHEN OTHERS THEN
DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no table exists');
END;


-- Split the Data into N1_TRAIN_DATA and N1_TEST_DATA 
EXECUTE IMMEDIATE 'CREATE OR REPLACE VIEW Wine2_TRAIN_DATA AS SELECT * FROM WineReviews130KTarget SAMPLE (60) SEED (1)';
DBMS_OUTPUT.PUT_LINE ('Created Wine2_TRAIN_DATA');
EXECUTE IMMEDIATE 'CREATE OR REPLACE VIEW Wine2_TEST_DATA AS SELECT * FROM WineReviews130Ktarget MINUS SELECT * FROM Wine2_TRAIN_DATA';
DBMS_OUTPUT.PUT_LINE ('Created Wine2_TEST_DATA');

-- Create a Build Setting (DT) for Model Build

EXECUTE IMMEDIATE 'CREATE TABLE Wine_build_settings (setting_name VARCHAR2(30),setting_value VARCHAR2(4000))';
EXECUTE IMMEDIATE 'INSERT INTO Wine_build_settings (setting_name, setting_value) VALUES (''ALGO_NAME'', ''ALGO_NAIVE_BAYES'')';
EXECUTE IMMEDIATE 'INSERT INTO Wine_build_settings (setting_name, setting_value) VALUES (''PREP_AUTO'', ''ON'')';
DBMS_OUTPUT.PUT_LINE ('Created model build settings table: Wine_build_settings ');

-- Build a Classification Model

EXECUTE IMMEDIATE 'CALL DBMS_DATA_MINING.CREATE_MODEL(''Wine_CLASS_MODEL'', ''CLASSIFICATION'', ''Wine2_TRAIN_DATA'', ''ID'','' POINTS_BIN'', ''Wine_build_settings'')';
DBMS_OUTPUT.PUT_LINE ('Created model: Wine_CLASS_MODEL ');

-- Test the Model by generating a apply result and then create a lift result

EXECUTE IMMEDIATE 'CALL DBMS_DATA_MINING.APPLY(''Wine_CLASS_MODEL'',''Wine2_TEST_DATA'',''ID'',''Wine_APPLY_RESULT'')';
DBMS_OUTPUT.PUT_LINE ('Created apply result: Wine_APPLY_RESULT ');
EXECUTE IMMEDIATE 'CALL DBMS_DATA_MINING.COMPUTE_LIFT(''Wine_APPLY_RESULT'',''Wine2_TEST_DATA'',''ID'',''POINTS_BIN'',''Wine_LIFT_TABLE'',''GT_90_POINTS'',''PREDICTION'',''PROBABILITY'',100)';
DBMS_OUTPUT.PUT_LINE ('Created lift result: Wine_LIFT_TABLE ');


END;

