%script


BEGIN
    FOR t IN (SELECT * FROM user_tables where TABLE_NAME IN ('IRIS','WINE','DIGITS') )
    LOOP   
        EXECUTE IMMEDIATE 'GRANT SELECT ON ' || t.table_name || ' TO PUBLIC';    
    END LOOP;
END;
/

%script

BEGIN
    FOR t IN (SELECT * FROM user_views where VIEW_NAME IN ('IRIS_VIEW','WINE_VIEW','DIGITS_VIEW') ) 
    LOOP   
        EXECUTE IMMEDIATE 'GRANT SELECT ON ' || t.view_name || ' TO PUBLIC';    
    END LOOP;
END;
/
