%script 

BEGIN DBMS_DATA_MINING.DROP_MODEL('AR_SH_SAMPLE');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;
BEGIN
    v_setlst('ALGO_NAME')           := 'ALGO_APRIORI_ASSOCIATION_RULES';
    v_setlst('PREP_AUTO')           := 'ON';
    v_setlst('ASSO_MIN_SUPPORT')    := '0.04';
    v_setlst('ASSO_MIN_CONFIDENCE') := '0.1';
    v_setlst('ASSO_MAX_RULE_LENGTH'):= '2';
    v_setlst('ODMS_ITEM_ID_COLUMN_NAME'):= 'PROD_NAME';

    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME        => 'AR_SH_SAMPLE',
        MINING_FUNCTION   => 'ASSOCIATION',
        DATA_QUERY        => 'select * from SALES_TRANS_CUST',
        SET_LIST          => v_setlst,
        CASE_ID_COLUMN_NAME => 'CUST_ID');
END;

%script

BEGIN EXECUTE IMMEDIATE 'DROP Table AR_SH_SAMPLE_SETTINGS';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
CREATE TABLE AR_SH_SAMPLE_SETTINGS (
  SETTING_NAME  VARCHAR2(30),
  SETTING_VALUE VARCHAR2(4000));

%script

BEGIN       
    INSERT INTO AR_SH_SAMPLE_SETTINGS VALUES (DBMS_DATA_MINING.ASSO_MIN_SUPPORT,0.04);
    INSERT INTO AR_SH_SAMPLE_SETTINGS VALUES (DBMS_DATA_MINING.ASSO_MIN_CONFIDENCE,0.1);
    INSERT INTO AR_SH_SAMPLE_SETTINGS VALUES (DBMS_DATA_MINING.ASSO_MAX_RULE_LENGTH,2);
    INSERT INTO AR_SH_SAMPLE_SETTINGS VALUES (DBMS_DATA_MINING.ODMS_ITEM_ID_COLUMN_NAME, 'PROD_NAME');
    COMMIT;
END;

%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('AR_SH_SAMPLE');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
BEGIN
    DBMS_DATA_MINING.CREATE_MODEL(
    MODEL_NAME          => 'AR_SH_SAMPLE',
    MINING_FUNCTION     => DBMS_DATA_MINING.ASSOCIATION,
    DATA_TABLE_NAME     => 'SALES_TRANS_CUST',
    CASE_ID_COLUMN_NAME => 'CUST_ID',
    SETTINGS_TABLE_NAME => 'AR_SH_SAMPLE_SETTINGS'
    );
END;
