    SETTINGS_TABLE_NAME => 'GLMC_SETTINGS',
    XFORM_LIST          => V_XLST);

    DBMS_OUTPUT.PUT_LINE ('Created model: GLMC_SH_REGR_SAMPLE ');
END;
