%r

dateFeatureExtraction <- function(dat, dateCol){

  datesIndex <- which(colnames(dat) == dateCol)
  colnames(dat)[datesIndex] <- "datesCol"

  dat <- cbind(dat, year = ore.year(dat$datesCol), month = ore.month(dat$datesCol),
               dayOfMonth = ore.mday(dat$datesCol), hour = ore.hour(dat$datesCol),
               minute = ore.minute(dat$datesCol), second = ore.second(dat$datesCol))

  colnames(dat)[datesIndex] <- dateCol

  return(dat)
}
