%script

CREATE OR REPLACE VIEW TRAIN_DATA_CLAS AS SELECT * FROM DEMOGRAPHICS4_V SAMPLE (60) SEED (1);
CREATE OR REPLACE VIEW TEST_DATA_CLAS AS SELECT * FROM DEMOGRAPHICS4_V MINUS SELECT * FROM TRAIN_DATA_CLAS;

%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('NB_CLASS_MODEL');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;

BEGIN
    v_setlst('PREP_AUTO') := 'ON';
    v_setlst('ALGO_NAME') := 'ALGO_NAIVE_BAYES';

    DBMS_DATA_MINING.CREATE_MODEL2(
        'NB_CLASS_MODEL',
        'CLASSIFICATION',
        'SELECT * FROM TRAIN_DATA_CLAS',
        v_setlst,
        'CUST_ID',
        'AFFINITY_CARD');
END;

%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('NB_CLASS_MODEL');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;

BEGIN
    v_setlst('PREP_AUTO') := 'ON';
    v_setlst('ALGO_NAME') := 'ALGO_NAIVE_BAYES';

    v_setlst('NABS_PAIRWISE_THRESHOLD') := '0';
    v_setlst('NABS_SINGLETON_THRESHOLD') := '0';


    DBMS_DATA_MINING.CREATE_MODEL2(
        'NB_CLASS_MODEL',
        'CLASSIFICATION',
        'SELECT * FROM TRAIN_DATA_CLAS',
        v_setlst,
        'CUST_ID',
        'AFFINITY_CARD');
END;

%script

BEGIN EXECUTE IMMEDIATE 'DROP TABLE APPLY_RESULT PURGE';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
BEGIN EXECUTE IMMEDIATE 'DROP TABLE LIFT_TABLE PURGE';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
BEGIN
  DBMS_DATA_MINING.APPLY('NB_CLASS_MODEL','TEST_DATA_CLAS','CUST_ID','APPLY_RESULT');
  DBMS_DATA_MINING.COMPUTE_LIFT('APPLY_RESULT','TEST_DATA_CLAS','CUST_ID','AFFINITY_CARD',
                                'LIFT_TABLE','1','PREDICTION','PROBABILITY',100);
END;

%sql 

SELECT CUST_ID, PREDICTION, ROUND(PROBABILITY,2) PROB, ROUND(COST,2) COST
FROM   APPLY_RESULT WHERE PREDICTION = ${PREDICTION='1','1'|'0'} 
AND    PROBABILITY > 0.7 
ORDER BY PROBABILITY DESC
FETCH FIRST 10 ROWS ONLY;

%script

BEGIN EXECUTE IMMEDIATE 'DROP TABLE N1_BUILD_SETTINGS PURGE';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
CREATE TABLE N1_BUILD_SETTINGS (setting_name VARCHAR2(30),setting_value VARCHAR2(4000));
/
BEGIN DBMS_DATA_MINING.DROP_MODEL('CLASS_MODEL_2');
EXCEPTION WHEN OTHERS THEN NULL; END;
/

BEGIN
  INSERT INTO N1_BUILD_SETTINGS (setting_name, setting_value) VALUES ('ALGO_NAME', 'ALGO_NAIVE_BAYES');
  INSERT INTO N1_BUILD_SETTINGS (setting_name, setting_value) VALUES ('PREP_AUTO', 'ON');

  DBMS_DATA_MINING.CREATE_MODEL('CLASS_MODEL_2', 'CLASSIFICATION', 'TRAIN_DATA_CLAS', 'CUST_ID',' AFFINITY_CARD', 'N1_BUILD_SETTINGS');
  DBMS_OUTPUT.PUT_LINE ('Created model: CLASS_MODEL_2 ');
END;
