%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('KM_SH_CLUS_SAMPLE');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;
BEGIN
    v_setlst('ALGO_NAME')        := 'ALGO_KMEANS';

    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME          => 'KM_SH_CLUS_SAMPLE',
        MINING_FUNCTION     => 'CLUSTERING',
        DATA_QUERY          => 'select * from CUSTOMERS360_V',
        SET_LIST            => v_setlst,
        CASE_ID_COLUMN_NAME => 'CUST_ID');
END;

%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('KM_SH_CLUS_SAMPLE');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;
BEGIN
    v_setlst('ALGO_NAME')        := 'ALGO_KMEANS';
    V_setlst('PREP_AUTO')        := 'ON';
    V_setlst('KMNS_DISTANCE')    := 'KMNS_EUCLIDEAN';
    V_setlst('KMNS_DETAILS')     := 'KMNS_DETAILS_ALL';
    V_setlst('KMNS_ITERATIONS')  := '3';
    V_setlst('KMNS_NUM_BINS')    := '10';

    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME          => 'KM_SH_CLUS_SAMPLE',
        MINING_FUNCTION     => 'CLUSTERING',
        DATA_QUERY          => 'select * from CUSTOMERS360_V',
        SET_LIST            => v_setlst,
        CASE_ID_COLUMN_NAME => 'CUST_ID');
END;

