%script 

BEGIN DBMS_DATA_MINING.DROP_MODEL('XGBOOST_CLASS_MODEL');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;

BEGIN
    v_setlst('PREP_AUTO')       := 'ON';
    v_setlst('ALGO_NAME')       := 'ALGO_XGBOOST';
    v_setlst('objective')       := 'binary:logistic';
    v_setlst('booster')         := 'dart';
    v_setlst('eval_metric')     := 'error,auc'; 
    v_setlst('num_round')       := '20';

    v_setlst('alpha')           := '0.1';
    v_setlst('eta')             := '0.3';
    v_setlst('lambda')          := '1';
    v_setlst('gamma')           := '0';
    v_setlst('max_delta_step')  := '0';
    v_setlst('max_depth')       := '6';
    v_setlst('min_child_weight') := '1';


    v_setlst('colsample_bylevel') := '0.9';
    v_setlst('colsample_bynode')  := '0.9';
    v_setlst('colsample_bytree')  := '0.9';

    v_setlst('one_drop')        := '0';
    v_setlst('normalize_type')  := 'tree';
    v_setlst('rate_drop')       := '0';

    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME          => 'XGBOOST_CLASS_MODEL',
        MINING_FUNCTION     => 'CLASSIFICATION',
        DATA_QUERY          => 'SELECT * FROM TRAIN_DATA_V',
        CASE_ID_COLUMN_NAME => 'CUST_ID',
        TARGET_COLUMN_NAME  => 'YRS_RESIDENCE',
        SET_LIST            => v_setlst);
END;
/

