%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('EM_SH_CLUS_SAMPLE_1');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;
BEGIN
    v_setlst('ALGO_NAME')        := 'ALGO_EXPECTATION_MAXIMIZATION';

    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME          => 'EM_SH_CLUS_SAMPLE_1',
        MINING_FUNCTION     => 'CLUSTERING',
        DATA_QUERY          => 'select * from CUSTOMERS360_V',
        SET_LIST            => v_setlst,
        CASE_ID_COLUMN_NAME => 'CUST_ID');
END;

%script

-- Note that additional settings are available in the documentation

BEGIN DBMS_DATA_MINING.DROP_MODEL('EM_SH_CLUS_SAMPLE_2');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;
BEGIN
    v_setlst('ALGO_NAME')               := 'ALGO_EXPECTATION_MAXIMIZATION';
    v_setlst('PREP_AUTO')               := 'ON';
    v_setlst('CLUS_NUM_CLUSTERS')       := '5';
    v_setlst('EMCS_MODEL_SEARCH')       := 'EMCS_MODEL_SEARCH_ENABLE';
    v_setlst('EMCS_CLUSTER_STATISTICS') := 'EMCS_CLUS_STATS_ENABLE';
    v_setlst('EMCS_NUM_ITERATIONS')     := '20';
    v_setlst('EMCS_NUM_QUANTILE_BINS')  := '10';
    v_setlst('EMCS_RANDOM_SEED')        := '123456';

    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME          => 'EM_SH_CLUS_SAMPLE_2',
        MINING_FUNCTION     => 'CLUSTERING',
        DATA_QUERY          => 'select * from CUSTOMERS360_V',
        SET_LIST            => v_setlst,
        CASE_ID_COLUMN_NAME => 'CUST_ID');
END;

%script 

BEGIN EXECUTE IMMEDIATE 'DROP TABLE EM_SH_SAMPLE_SETTINGS PURGE';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
CREATE TABLE EM_SH_SAMPLE_SETTINGS (SETTING_NAME  VARCHAR2(30),
                                    SETTING_VALUE VARCHAR2(4000));

-- See documentation for additional settings

BEGIN DBMS_DATA_MINING.DROP_MODEL('EM_SH_CLUS_SAMPLE_3');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
BEGIN       
   INSERT INTO EM_SH_SAMPLE_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.ALGO_NAME,               DBMS_DATA_MINING.ALGO_EXPECTATION_MAXIMIZATION);
   INSERT INTO EM_SH_SAMPLE_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.EMCS_MODEL_SEARCH,       DBMS_DATA_MINING.EMCS_MODEL_SEARCH_ENABLE);
   INSERT INTO EM_SH_SAMPLE_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.PREP_AUTO,               DBMS_DATA_MINING.PREP_AUTO_ON);
   INSERT INTO EM_SH_SAMPLE_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.EMCS_CLUSTER_STATISTICS, DBMS_DATA_MINING.EMCS_CLUS_STATS_ENABLE);
END;

%sql
