%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('OC_SH_CLUS_SAMPLE');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;
BEGIN
    v_setlst('ALGO_NAME')        := 'ALGO_O_CLUSTER';
    V_setlst('PREP_AUTO')        := 'ON';

    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME          => 'OC_SH_CLUS_SAMPLE',
        MINING_FUNCTION     => 'CLUSTERING',
        DATA_QUERY          => 'select * from CUSTOMERS360_V',
        SET_LIST            => v_setlst,
        CASE_ID_COLUMN_NAME => 'CUST_ID');
END;

%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('OC_SH_CLUS_SAMPLE');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;
BEGIN
    v_setlst('ALGO_NAME')               := 'ALGO_O_CLUSTER';
    V_setlst('PREP_AUTO')               := 'ON';
    V_setlst('CLUS_NUM_CLUSTERS')       := '5';
    V_setlst('OCLT_SENSITIVITY')        := '0.5';

    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME          => 'OC_SH_CLUS_SAMPLE',
        MINING_FUNCTION     => 'CLUSTERING',
        DATA_QUERY          => 'select * from CUSTOMERS360_V',
        SET_LIST            => v_setlst,
        CASE_ID_COLUMN_NAME => 'CUST_ID');
END;

%script 

BEGIN EXECUTE IMMEDIATE 'DROP TABLE OC_SH_SAMPLE_SETTINGS PURGE';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
CREATE TABLE OC_SH_SAMPLE_SETTINGS (SETTING_NAME  VARCHAR2(30),
                                    SETTING_VALUE VARCHAR2(4000));
/
BEGIN DBMS_DATA_MINING.DROP_MODEL('OC_SH_CLUS_SAMPLE');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
BEGIN       
   INSERT INTO OC_SH_SAMPLE_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.OCLT_SENSITIVITY,'0.5');
   INSERT INTO OC_SH_SAMPLE_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.PREP_AUTO,DBMS_DATA_MINING.PREP_AUTO_ON);
END;

%sql
