%script

BEGIN
    EXECUTE IMMEDIATE 'DROP TABLE missc_tbl';
    EXCEPTION WHEN OTHERS THEN NULL;
END;
/

BEGIN
  DBMS_DATA_MINING_TRANSFORM.CREATE_MISS_CAT(
      MISS_TABLE_NAME   => 'MISSC_TBL');
  DBMS_DATA_MINING_TRANSFORM.INSERT_MISS_CAT_MODE(
      MISS_TABLE_NAME   => 'MISSC_TBL',
      DATA_TABLE_NAME   => 'CUSTOMER_MISSING_V');
END;
/

%script

BEGIN
    EXECUTE IMMEDIATE 'DROP VIEW  CUSTOMER_INSURANCE_LTV_SQL_STACK';
    EXCEPTION WHEN OTHERS THEN NULL;
END;
/
DECLARE
    xforms      dbms_data_mining_transform.TRANSFORM_LIST;
BEGIN
    DBMS_DATA_MINING_TRANSFORM.STACK_MISS_CAT (
        MISS_TABLE_NAME    => 'MISSC_TBL',
        XFORM_LIST         => xforms);
    DBMS_DATA_MINING_TRANSFORM.XFORM_STACK (
         XFORM_LIST         => xforms,
         DATA_TABLE_NAME    => 'CUSTOMER_INSURANCE_LTV_SQL',
         XFORM_VIEW_NAME    => 'CUSTOMER_INSURANCE_LTV_SQL_STACK');
END;

%script

BEGIN
    EXECUTE IMMEDIATE 'DROP TABLE miss_num_xtbl';
    EXCEPTION WHEN OTHERS THEN NULL;
END;
/

BEGIN
   DBMS_DATA_MINING_TRANSFORM.CREATE_MISS_NUM(
       MISS_TABLE_NAME   => 'MISS_NUM_XTBL');
   DBMS_DATA_MINING_TRANSFORM.INSERT_MISS_NUM_MEAN(
       MISS_TABLE_NAME   => 'miss_num_xtbl',
       DATA_TABLE_NAME   => 'CUSTOMER_MISSING_V',
       EXCLUDE_LIST      => DBMS_DATA_MINING_TRANSFORM.COLUMN_LIST('CUSTOMER_ID'));
END;
/

