%script

BEGIN
    EXECUTE IMMEDIATE 'DROP TABLE norm_zscore_tbl';
    EXCEPTION WHEN OTHERS THEN NULL;
END;

BEGIN
    dbms_data_mining_transform.CREATE_NORM_LIN (
       norm_table_name  => 'norm_zscore_tbl');
       dbms_data_mining_transform.INSERT_NORM_LIN_ZSCORE (
       norm_table_name  => 'norm_zscore_tbl',
       data_table_name  => 'CUSTOMER_INSURANCE_LTV_SQL',
       exclude_list     => dbms_data_mining_transform.COLUMN_LIST('CUSTOMER_ID'),
       round_num        => 3);
END;

%sql

SELECT col, shift, scale 
FROM norm_zscore_tbl;

