%script 


SET serveroutput ON;
DECLARE
    tbl VARCHAR2(100);
    fmt VARCHAR2(2000);
BEGIN
    tbl := 'CUSTOMER_INSURANCE_LTV_SQL';

    fmt := 'select ';

    FOR rec IN (SELECT column_name, data_type FROM user_tab_cols WHERE table_name = tbl and data_type = 'NUMBER' )
    LOOP
        fmt := fmt||'case when '||rec.column_name||' <= 0 then 0 else log(10,'||rec.column_name||') end as log_'||rec.column_name||',';
    END LOOP;

    fmt := 'create or replace view customer_log_v as ' ||substr(fmt, 0, length(fmt)-1)||' from '||tbl;
    EXECUTE IMMEDIATE fmt;
    --dbms_output.put_line(fmt);
END;
