%script

BEGIN 
    DBMS_DATA_MINING.DROP_MODEL('SVD_CUST_LTV_MDL');
    EXCEPTION WHEN OTHERS THEN NULL; 
END;
/

DECLARE
    V_SETLST DBMS_DATA_MINING.SETTING_LIST;
BEGIN
    V_SETLST('ALGO_NAME')  := 'ALGO_SINGULAR_VALUE_DECOMP';
    V_SETLST('SVDS_SCORING_MODE') := 'SVDS_SCORING_PCA';
    V_SETLST('PREP_AUTO')  := 'OFF';
    V_SETLST('PREP_SCALE_2DNUM') := 'PREP_SCALE_STDDEV';
    V_SETLST('PREP_SHIFT_2DNUM') := 'PREP_SHIFT_MEAN';
    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME          => 'SVD_CUST_LTV_MDL',
        MINING_FUNCTION     => 'FEATURE_EXTRACTION',
        CASE_ID_COLUMN_NAME => 'CUSTOMER_ID',
        DATA_QUERY          => 'SELECT * FROM CUST_V',
        SET_LIST            => V_SETLST);
END;

%script

BEGIN EXECUTE IMMEDIATE 'DROP TABLE SVD_CUSTOMER_LTV_SETTINGS';
EXCEPTION WHEN OTHERS THEN NULL; END;
/

%script

BEGIN       
  INSERT INTO SVD_CUSTOMER_LTV_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES
    (DBMS_DATA_MINING.ALGO_NAME, DBMS_DATA_MINING.ALGO_SINGULAR_VALUE_DECOMP);
  INSERT INTO SVD_CUSTOMER_LTV_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES
    (DBMS_DATA_MINING.PREP_AUTO, DBMS_DATA_MINING.PREP_AUTO_OFF);  
  INSERT INTO SVD_CUSTOMER_LTV_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES
    (DBMS_DATA_MINING.SVDS_SCORING_MODE, DBMS_DATA_MINING.SVDS_SCORING_PCA);
  INSERT INTO SVD_CUSTOMER_LTV_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES
    (DBMS_DATA_MINING.PREP_SHIFT_2DNUM, DBMS_DATA_MINING.PREP_SHIFT_MEAN); 
  INSERT INTO SVD_CUSTOMER_LTV_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES
    (DBMS_DATA_MINING.PREP_SCALE_2DNUM, DBMS_DATA_MINING.PREP_SCALE_STDDEV);   
END;

%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('SVD_CUST_LTV_MDL');
EXCEPTION WHEN OTHERS THEN NULL; END;
/

BEGIN
  DBMS_DATA_MINING.CREATE_MODEL(
    MODEL_NAME          => 'SVD_CUST_LTV_MDL',
    MINING_FUNCTION     => DBMS_DATA_MINING.FEATURE_EXTRACTION,
    DATA_TABLE_NAME     => 'CUST_V',
    CASE_ID_COLUMN_NAME => 'CUSTOMER_ID',
    SETTINGS_TABLE_NAME => 'SVD_CUSTOMER_LTV_SETTINGS');
END;
/
