%script

SET SERVEROUTPUT ON;
DECLARE
    CNT INT;
    TOTAL INT;
    RATIO NUMBER;
    SELECTED_COLS VARCHAR2(1000);
BEGIN

    RATIO := 0.95;
    SELECT COUNT(1) 
    INTO TOTAL
    FROM CUSTOMER_INSURANCE_LTV_SQL;

    FOR REC IN (SELECT COLUMN_NAME, DATA_TYPE FROM USER_TAB_COLS WHERE 
                TABLE_NAME = 'CUSTOMER_INSURANCE_LTV_SQL' 
                AND DATA_TYPE IN ('CHAR', 'VARCHAR2'))
    LOOP

        SELECT NUM_DISTINCT
        INTO CNT
        FROM USER_TAB_COL_STATISTICS
        WHERE TABLE_NAME = 'CUSTOMER_INSURANCE_LTV_SQL'
        AND COLUMN_NAME = REC.COLUMN_NAME;
        IF CNT > 1 AND CNT < RATIO*TOTAL THEN
            DBMS_OUTPUT.PUT_LINE(REC.COLUMN_NAME);    
            IF SELECTED_COLS IS NULL THEN 
                SELECTED_COLS := REC.COLUMN_NAME;
            ELSE
                SELECTED_COLS := SELECTED_COLS||','||REC.COLUMN_NAME;
            END IF;

        END IF;

    END LOOP;
    EXECUTE IMMEDIATE 'CREATE OR REPLACE VIEW SELECTED_NO_IDENTIFIED_V AS SELECT '||SELECTED_COLS||' FROM CUSTOMER_INSURANCE_LTV_SQL';
END;

