%script

SET SERVEROUTPUT ON;
DECLARE
    CNT INT;
    TOTAL INT;
    RATIO NUMBER;
    TBL VARCHAR2(100);
    FMT VARCHAR2(2000);
    STMT VARCHAR2(2000);
    SELECTED_COLS VARCHAR2(1000);
BEGIN
    RATIO := 0.90;
    TBL := 'CUSTOMER_INSURANCE_LTV_SQL';
    SELECT COUNT(1) 
    INTO TOTAL
    FROM CUSTOMER_INSURANCE_LTV_SQL;

    FMT := 'SELECT MAX(CNT) FROM ( SELECT <COL>, COUNT(1) CNT FROM <TBL> GROUP BY <COL> ) A';

    FOR REC IN (SELECT COLUMN_NAME, DATA_TYPE 
                FROM USER_TAB_COLS 
                WHERE TABLE_NAME = TBL )
    LOOP
        STMT := REPLACE(FMT, '<COL>', REC.COLUMN_NAME);
        STMT := REPLACE(STMT, '<TBL>', TBL);
        EXECUTE IMMEDIATE STMT INTO CNT;
        IF CNT > TOTAL*RATIO THEN
            DBMS_OUTPUT.PUT_LINE(REC.COLUMN_NAME);    
        ELSE
            IF SELECTED_COLS IS NULL THEN 
                SELECTED_COLS := REC.COLUMN_NAME;
            ELSE
                SELECTED_COLS := SELECTED_COLS||','||REC.COLUMN_NAME;
            END IF;
        END IF;

    END LOOP;
    EXECUTE IMMEDIATE 'CREATE OR REPLACE VIEW SELECTED_NO_CONSTANT_V AS SELECT '||SELECTED_COLS||' FROM CUSTOMER_INSURANCE_LTV_SQL';
END;
