%script

BEGIN EXECUTE IMMEDIATE 'DROP TABLE CUSTOMERS_DEMO';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
CREATE TABLE CUSTOMERS_DEMO AS 
  SELECT A.CUST_ID, A.CUST_GENDER, A.CUST_MARITAL_STATUS, A.CUST_YEAR_OF_BIRTH, A.CUST_INCOME_LEVEL, A.CUST_CREDIT_LIMIT, 
         B.EDUCATION, B.AFFINITY_CARD, B.HOUSEHOLD_SIZE, B.OCCUPATION, B.YRS_RESIDENCE, B.Y_BOX_GAMES
  FROM SH.CUSTOMERS A, SH.SUPPLEMENTARY_DEMOGRAPHICS B
  WHERE A.CUST_ID = B.CUST_ID;

%sql

SELECT PARTITION_NAME, ATTRIBUTE_NAME, ATTRIBUTE_VALUE, round(COEFFICIENT,3) COEFFICIENT, round(REVERSED_COEFFICIENT,3) REVERSED_COEFFICIENT 
FROM DM$VLSVM_MOD_PARTITIONED;
