%script

CREATE OR REPLACE PROCEDURE IMPORT_DATA_OBJSTORE_DATASET(
  DATASET IN VARCHAR2,
  VERBOSE IN BOOLEAN DEFAULT FALSE,
  LOCATION_URI IN VARCHAR2 DEFAULT 'https://objectstorage.us-ashburn-1.oraclecloud.com/n/adwc4pm/b/OML_Data/o/') AUTHID CURRENT_USER AS
dataset_upper VARCHAR2(100);
file_name VARCHAR2(1000);
table_name varchar2(1000);
obj varchar2(1000);
sql_file varchar2(1000);
b_data BLOB;
v_data VARCHAR2(5000);
v_rownum INTEGER;

------------------------------------------------------
-- Declare a new variable to convert the input
-- dataset to upper case.  

------------------------------------------------------

BEGIN
    dataset_upper := upper(dataset);

------------------------------------------------------
-- Define file name from input dataset
------------------------------------------------------

SELECT CONCAT(dataset_upper, '.csv') INTO FILE_NAME from dual;

IF VERBOSE THEN
   DBMS_OUTPUT.PUT_LINE('Dataset name is: '||dataset_upper);
   DBMS_OUTPUT.PUT_LINE('CSV file name is: '||file_name);
END IF;

------------------------------------------------------
-- Select file from Object Storage
------------------------------------------------------

SELECT object_name INTO obj FROM DBMS_CLOUD.LIST_OBJECTS(NULL, LOCATION_URI) where object_name = FILE_NAME;

------------------------------------------------------
-- Define table name
------------------------------------------------------

SELECT REGEXP_SUBSTR(obj, '[^.]+') INTO table_name from dual;

   DBMS_OUTPUT.PUT_LINE('Table name is: '||table_name);

------------------------------------------------------
-- Define file containing the CREATE TABLE statement,
-- which is saved in a TXT file. DBMS_CLOUD does not
-- support reading SQL files.
------------------------------------------------------

SELECT table_name||'.txt' INTO sql_file from dual;

IF VERBOSE THEN
   DBMS_OUTPUT.PUT_LINE('Text file is: '||sql_file);
END IF;

------------------------------------------------------
-- Read the SQL statement from the text file into
-- a binary variable and execute the statement
------------------------------------------------------

BEGIN
  b_data := DBMS_CLOUD.GET_OBJECT(
      credential_name => NULL,
      object_uri => LOCATION_URI||sql_file);
  v_data :=  utl_raw.cast_to_varchar2(b_data);
  IF VERBOSE THEN
  DBMS_OUTPUT.PUT_LINE('Table definition is: '||v_data);
  END IF;
  EXECUTE IMMEDIATE v_data;
END;

------------------------------------------------------
-- Write data to the final table specified as dataset
-- input. If the table already exists, an error is
-- returned.
------------------------------------------------------

BEGIN
    DBMS_CLOUD.COPY_DATA (
       credential_name => NULL,
       table_name => table_name,
       file_uri_list   => LOCATION_URI||file_name,
  format => json_object('type' value 'CSV', 'skipheaders' value 1));
END;

------------------------------------------------------
-- Return the number of rows in the table
------------------------------------------------------

EXECUTE IMMEDIATE 'SELECT count(*) FROM ' || table_name INTO v_rownum;
DBMS_OUTPUT.PUT_LINE ('Table created with number of rows = ' || v_rownum);

END;

