%script 

-- Drop any previous GLM Model Settings table for notebook repeatability
BEGIN EXECUTE IMMEDIATE 'DROP TABLE GLMR_SETTINGS';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
-- Drop any previous Model Diagnostics table for notebook repeatability
BEGIN EXECUTE IMMEDIATE 'DROP TABLE GLMR_DIAG';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
--Create Model Settings Table
CREATE TABLE GLMR_SETTINGS (
    SETTING_NAME  VARCHAR2(30),
    SETTING_VALUE VARCHAR2(4000));

%script 

BEGIN 
    INSERT INTO GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.ALGO_NAME, DBMS_DATA_MINING.ALGO_GENERALIZED_LINEAR_MODEL);
    INSERT INTO  GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.PREP_AUTO, DBMS_DATA_MINING.PREP_AUTO_ON);  

-- output row diagnostic statistics into a table named GLMR_SH_SAMPLE_DIAG  
    INSERT INTO  GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.GLMS_DIAGNOSTICS_TABLE_NAME, 'GLMR_SH_SAMPLE_DIAG');  

-- turn ridge regression on or off. By default the system turns it on if there is a multicollinearity.  Ridge regression cannot be used with feature selection and feature generation, so we will keep it off in this example
     INSERT INTO  GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.GLMS_RIDGE_REGRESSION, 'GLMS_RIDGE_REG_DISABLE');    

-- turn on feature selection. By default the system does not do feature selection. Some options for feature selection are show below too
    INSERT INTO  GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.GLMS_FTR_SELECTION, DBMS_DATA_MINING.GLMS_FTR_SELECTION_ENABLE);
    INSERT INTO  GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.GLMS_MAX_FEATURES, '30');
    INSERT INTO  GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.GLMS_PRUNE_MODEL, DBMS_DATA_MINING.GLMS_PRUNE_MODEL_ENABLE);  

-- turn on feature generation. We will do Quadratic features, but Cubic is also available. Feature generation requires feature selection to be enabled.
    INSERT INTO  GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.GLMS_FTR_GENERATION, DBMS_DATA_MINING.GLMS_FTR_GENERATION_ENABLE);
    INSERT INTO  GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.GLMS_FTR_GEN_METHOD, DBMS_DATA_MINING.GLMS_FTR_GEN_QUADRATIC);
--    INSERT INTO  GLMR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.GLMS_FTR_GEN_METHOD, DBMS_DATA_MINING.GLMS_FTR_GEN_CUBIC);

END;

