-- Switch between Kernel Types - Linear or Gaussian.  By default the system uses the Linear Kernel.
    INSERT INTO SVMLR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.SVMS_KERNEL_FUNCTION, DBMS_DATA_MINING.SVMS_LINEAR);
