    INSERT INTO SVMLR_SETTINGS (SETTING_NAME, SETTING_VALUE) VALUES (DBMS_DATA_MINING.SVMS_REGULARIZER, DBMS_DATA_MINING.SVMS_REGULARIZER_L1);
