%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('T_SVM_Clas_sample');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
 v_setlst DBMS_DATA_MINING.SETTING_LIST;
 xformlist dbms_data_mining_transform.TRANSFORM_LIST;

BEGIN
 v_setlst(dbms_data_mining.algo_name)              := dbms_data_mining.algo_support_vector_machines;
 v_setlst(dbms_data_mining.prep_auto)              := dbms_data_mining.prep_auto_on;
 v_setlst(dbms_data_mining.svms_kernel_function)   := dbms_data_mining.svms_linear;
 v_setlst(dbms_data_mining.svms_complexity_factor) := '100';
 v_setlst(dbms_data_mining.odms_text_policy_name)  := 'DMDEMO_SVM_POLICY';
 v_setlst(dbms_data_mining.svms_solver)            := dbms_data_mining.svms_solver_sgd;

 dbms_data_mining_transform.SET_TRANSFORM(xformlist, 'comments', null, 'comments', null, 'TEXT');

 DBMS_DATA_MINING.CREATE_MODEL2(model_name => 'T_SVM_CLAS_SAMPLE',
                                mining_function => dbms_data_mining.classification,
                                data_query => 'select * from mining_train_text',
                                set_list => v_setlst,
                                case_id_column_name => 'cust_id',
                                target_column_name => 'affinity_card',
                                xform_list => xformlist);
END;
/

%script

BEGIN
 EXECUTE IMMEDIATE 'DROP TABLE MINING_APPLY_RESULT PURGE';
 EXECUTE IMMEDIATE 'DROP TABLE T_LIFT_TABLE PURGE';
EXCEPTION WHEN OTHERS THEN NULL; END;
/

BEGIN
 DBMS_DATA_MINING.APPLY('T_SVM_CLAS_SAMPLE','mining_test_text','CUST_ID','MINING_APPLY_RESULT');
 DBMS_DATA_MINING.COMPUTE_LIFT('MINING_APPLY_RESULT','mining_test_text','CUST_ID',
                               'AFFINITY_CARD','T_LIFT_TABLE','1','PREDICTION','PROBABILITY',100);
END;

