%script

BEGIN CTX_DDL.DROP_POLICY('WIKI_TXTPOL'); 
  EXCEPTION WHEN OTHERS THEN NULL; 
END;

BEGIN CTX_DDL.DROP_PREFERENCE( 'WIKI_LEXER'); 
  EXCEPTION WHEN OTHERS THEN NULL; 
END;

BEGIN CTX_DDL.CREATE_PREFERENCE('WIKI_LEXER', 
                                'BASIC_LEXER'); 
END;

BEGIN CTX_DDL.SET_ATTRIBUTE('WIKI_LEXER', 
                            'INDEX_STEMS', 
                             'ENGLISH'); 
END;

BEGIN CTX_DDL.CREATE_POLICY(POLICY_NAME => 'WIKI_TXTPOL', 
                            LEXER => 'WIKI_LEXER'); 
END;
