%script
/* Build a classification model and then generate a lift test result and an apply result.  */

DECLARE
v_sql varchar2(100);

BEGIN


/*  drop build settings  */
BEGIN
v_sql := 'DROP TABLE n1_build_settings_ltv PURGE';
EXECUTE IMMEDIATE v_sql;
DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
WHEN OTHERS THEN
DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no table exists');
END;

/*  drop model  */
BEGIN
v_sql := 'CALL DBMS_DATA_MINING.DROP_MODEL(''N1_CLASS_MODEL_DT'')';
EXECUTE IMMEDIATE v_sql;
DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
WHEN OTHERS THEN
DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no model exists');
END;

/*  drop apply result  */
BEGIN
v_sql := 'DROP TABLE N1_APPLY_RESULT_DT PURGE';
EXECUTE IMMEDIATE v_sql;
DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
WHEN OTHERS THEN
DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no table exists');
END;

/*  drop lift result  */
BEGIN
v_sql := 'DROP TABLE N1_LIFT_TABLE_DT PURGE';
EXECUTE IMMEDIATE v_sql;
DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
WHEN OTHERS THEN
DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no table exists');
END;


/*  Split the Data into N1_TRAIN_DATA and N1_TEST_DATA  */
EXECUTE IMMEDIATE 'CREATE OR REPLACE VIEW N1_TRAIN_DATA_LTV AS SELECT * FROM PREDICT_LTV_BIN SAMPLE (60) SEED (1)';
DBMS_OUTPUT.PUT_LINE ('Created N1_TRAIN_DATA_LTV');
EXECUTE IMMEDIATE 'CREATE OR REPLACE VIEW N1_TEST_DATA_LTV AS SELECT * FROM PREDICT_LTV_BIN MINUS SELECT * FROM N1_TRAIN_DATA_LTV';
DBMS_OUTPUT.PUT_LINE ('Created N1_TEST_DATA_LTV');

/*  Create a Build Setting (DT) for Model Build  */

EXECUTE IMMEDIATE 'CREATE TABLE n1_build_settings_ltv (setting_name VARCHAR2(30),setting_value VARCHAR2(4000))';
EXECUTE IMMEDIATE 'INSERT INTO n1_build_settings_ltv (setting_name, setting_value) VALUES (''ALGO_NAME'', ''ALGO_DECISION_TREE'')';
EXECUTE IMMEDIATE 'INSERT INTO n1_build_settings_ltv (setting_name, setting_value) VALUES (''PREP_AUTO'', ''ON'')';
DBMS_OUTPUT.PUT_LINE ('Created model build settings table: n1_build_settings_ltv ');

/*  Build a Classification Model  */

EXECUTE IMMEDIATE 'CALL DBMS_DATA_MINING.CREATE_MODEL(''N1_CLASS_MODEL_DT'', ''CLASSIFICATION'', ''N1_TRAIN_DATA_LTV'', ''CUSTOMER_ID'',''LIFETIME_VALUE_CATEGORY'', ''n1_build_settings_ltv'')';
DBMS_OUTPUT.PUT_LINE ('Created model: N1_CLASS_MODEL_DT ');

/*  Test the Model by generating a apply result and then create a lift result  */

EXECUTE IMMEDIATE 'CALL DBMS_DATA_MINING.APPLY(''N1_CLASS_MODEL_DT'',''N1_TEST_DATA_LTV'',''CUSTOMER_ID'',''N1_APPLY_RESULT_DT'')';
DBMS_OUTPUT.PUT_LINE ('Created apply result: N1_APPLY_RESULT_DT ');
EXECUTE IMMEDIATE 'CALL DBMS_DATA_MINING.COMPUTE_LIFT(''N1_APPLY_RESULT_DT'',''N1_TEST_DATA_LTV'',''CUSTOMER_ID'',''LIFETIME_VALUE_CATEGORY'',''N1_LIFT_TABLE_DT'',''High'',''PREDICTION'',''PROBABILITY'',100)';
DBMS_OUTPUT.PUT_LINE ('Created lift result: N1_LIFT_TABLE_DT ');


END;

