%script
BEGIN EXECUTE IMMEDIATE 'DROP VIEW ltv_bin_apply_data_v_rf';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
CREATE VIEW ltv_bin_apply_data_v_rf
AS 
SELECT 
"CUSTOMER_ID", t.* FROM N1_TEST_DATA_LTV s, TABLE(PREDICTION_SET(RF_MLS_LTV_BIN USING *)) t;

%script
BEGIN EXECUTE IMMEDIATE 'DROP TABLE ltv_bin_rf_confusion_matrix';
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_accuracy    NUMBER;
BEGIN
    DBMS_DATA_MINING.COMPUTE_CONFUSION_MATRIX (
        accuracy => v_accuracy,
        apply_result_table_name      => 'N1_APPLY_RESULT_RF',
        target_table_name            => 'N1_TEST_DATA_LTV',
        case_id_column_name          => 'customer_id',
        target_column_name           => 'lifetime_value_category',
        confusion_matrix_table_name  => 'ltv_bin_rf_confusion_matrix',
        score_column_name            => 'PREDICTION',
        score_criterion_column_name  => 'PROBABILITY',
        score_criterion_type         => 'PROBABILITY');
    DBMS_OUTPUT.PUT_LINE('**** MODEL ACCURACY ****: ' || ROUND(v_accuracy * 100,4));
END;
/

%sql
/* We want actual_target_vale as rows and predicted_target_value as column and then value as metric */
select * from (
  select actual_target_value, predicted_target_value, value  
  from ltv_bin_rf_confusion_matrix) m
pivot ( 
  max(value)
  for predicted_target_value in ( 'Low' "Predicted LOW", 'Medium' "Predicted MEDIUM", 'High' "Predicted HIGH")
)
order by actual_target_value;

