%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('GLM_MODEL');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;
    
BEGIN
    v_setlst('PREP_AUTO') := 'ON';
    v_setlst('ALGO_NAME') := 'ALGO_GENERALIZED_LINEAR_MODEL';
    v_setlst('GLMS_DIAGNOSTICS_TABLE_NAME') := 'GLMR_SH_SAMPLE_DIAG';
    v_setlst('GLMS_FTR_SELECTION') := 'GLMS_FTR_SELECTION_ENABLE';
    v_setlst('GLMS_FTR_GENERATION') := 'GLMS_FTR_GENERATION_ENABLE';
    
    
    DBMS_DATA_MINING.CREATE_MODEL2(
        'GLM_MODEL',
        'REGRESSION',
        'SELECT * FROM IRIS_DATA',
        v_setlst,
        'SEPAL_WIDTH',
        'PETAL_WIDTH');
END;
/

