drop table html_files;
create table html_files (filename varchar2(255), filedata varchar2(255));


alter table html_files add (pk number primary key);
update html_files set pk = rownum;
commit;

exec ctx_ddl.drop_section_group ('msg')
exec ctx_ddl.create_section_group ('msg', 'html_section_group')

set timing on

create index html_files_index on html_files (filedata)
indextype is ctxsys.context 
parameters ('datastore ctxsys.file_datastore filter ctxsys.null_filter section group msg');


